# Euler Problem 8
# Find the greatest product of five consecutive digits in the 1000-digit number.

turd_ary = [].to_s.chars.map(&:to_i)
turd_ary.delete_at(0)
turd_ary.delete_at(1000)
turd_ary.delete_at(1001)

fart_ary = Array.new
poop_ary = Array.new
integer = 0

poop_ary.push(turd_ary[integer] * turd_ary[integer+1] * turd_ary[integer+2] * turd_ary[integer+3] * turd_ary[integer+4] )

	while integer < turd_ary.count-4
	fart_ary.push(turd_ary[integer] * turd_ary[integer+1] * turd_ary[integer+2] * turd_ary[integer+3] * turd_ary[integer+4] )
		if fart_ary.first > poop_ary.first
			poop_ary.clear
			poop_ary.push(fart_ary.first)
		end
	fart_ary.clear
	integer = integer +1
	end

puts poop_ary
puts turd_ary